<?php		if(!empty($_POST))	{		$msg=array();				if(empty($_POST['cname']) || empty($_POST['cemail']) || empty($_POST['cquery']))		{			$msg[]="Please full fill all requirement";		}								if(is_numeric($_POST['cname']))		{			$msg[]="Name must be in String Format...";		}				if(is_numeric($_POST['cemail']))	//See this validation for @,21212 Later		{			$msg[]="Name must be in appropriate Format...";		}						if(!empty($msg))		{			echo '<b>Error:-</b><br>';						foreach($msg as $k)			{				echo '<li>'.$k;			}		}		else		{			$cname=$_POST['cname'];			$cemail=$_POST['cemail'];			$cquery=$_POST['cquery'];						$link=mysql_connect("localhost","root","")or die("Can't Connect...");						mysql_select_db("event",$link) or die("Can't Connect to Database...");						$query="insert into contact(cname,cemail,cquery)			values('$cname','$cemail','$cquery')";						mysql_query($query,$link) or die("Can't Execute Query...");						header("location:contact.php");		}	}	else	{		header("location:index.php");	}?>